    // add a new task to the type input
    addNewTask() {
        let type = this.typeInput;
        let task = this.taskInput;
        let bgcolor = this.selectedColor;

        if (task) {

            this.dbService.getAll(this.table).subscribe((res: any) => {
                let alldata = res;
                this.dbService.clear(this.table).subscribe((deleted: any) => {
                    this.dbService.add(this.table, {
                        type: type.length > 0 ? type : this.randomEmoji(),
                        task,
                        updated_at: Date.now(),
                        bgcolor,
                    }).subscribe((key) => {

                        this.dbService.bulkAdd(this.table, alldata.map((item: any) => { delete item.id; return item })).subscribe((result: any) => {
                            this.onSave.emit('refresh');
                            this.onClose();
                        })


                    });
                });
            })


            this.dbService.add(this.table, {

                type,
                task,
                updated_at: Date.now(),
                bgcolor,
            }).subscribe((key) => {
                this.onSave.emit('refresh');
                this.onClose();
            });
        }

        else {
            this.toastService.error('Please write your task');
        }


    }


    @Output() close = new EventEmitter();

    onClose() {
        this.close.emit(null);
    }


